\\n        ,ggba.field_1 `物料基础属性field1`\\n        ,ggba.field_2 `物料基础属性field2`\\n        ,ggba.field_3 `物料基础属性field3`\\n        ,ggba.field_4 `物料基础属性field4`\\n        ,ggba.field_5 `物料基础属性field5`\\n        ,ggba.field_59 `物料基础属性field59`\\n        ,gsp.field_1 `物料销售属性field1`\\n        ,gsp.field_2 `物料销售属性field2`\\n        ,gsp.field_3 `物料销售属性field3`\\n        ,gsp.field_4 `物料销售属性field4`\\n        ,gsp.field_5 `物料销售属性field5`\\n        ,t.stock_date\\n        ,t.period_code\\n        ,t.time_point `时间节点`\\n        ,sstc.code\\n        ,sstc.name `事务分类`\\n        ,t.qty `数量`\\n  from (\\n          select \\n               sslw.stock_organization_id stock_organization_id\\n              ,sslw.logic_warehouse_id logic_warehouse_id\\n              ,sslw.logic_whareatype_id logic_whareatype_id\\n              ,sslw.owner_id owner_id\\n              ,sslw.business_party_id business_party_id\\n              ,sslw.transaction_class_id transaction_class_id \\n              ,(CASE WHEN sslw.stock_type=\'1\' THEN \'经销\' \\n                     WHEN sslw.stock_type=\'2\' THEN \'代销\' \\n                     WHEN sslw.stock_type is NULL THEN NULL \\n                     ELSE \'其它\' END) stock_type\\n              ,sslw.goods_id goods_id\\n              ,sslw.sku_id sku_id\\n              ,sslw.attr1 attr1\\n              ,sslw.attr2 attr2\\n              ,sslw.attr3 attr3\\n              ,sslw.attr4 attr4\\n              ,sslw.attr5 attr5\\n              ,sslw.stock_date  as stock_date\\n              ,\'\' as period_code\\n              ,sslw.change_qty qty\\n              ,\'期间\' time_point\\n           from cdm.ods_stk_stock_logic_workflow sslw\\n          where sslw.qty_type in (\'1\', \'31\', \'32\')         \\n          union all         \\n          SELECT \\n               fsps.stock_organization_id stock_organization_id\\n              ,fsps.logic_warehouse_id logic_warehouse_id\\n              ,fsps.logic_whareatype_id  logic_whareatype_id\\n              ,fsps.owner_id  owner_id\\n              ,null business_party_id\\n              ,fsps.transaction_class_id transaction_class_id \\n              ,fsps.stock_type  stock_type\\n              ,fsps.goods_id goods_id \\n              ,fsps.sku_id sku_id\\n              ,fsps.attr1 attr1\\n              ,fsps.attr2 attr2\\n              ,fsps.attr3 attr3\\n              ,fsps.attr4 attr4\\n              ,fsps.attr5 attr5\\n              ,null as stock_date \\n              ,fsps.period_code as period_code\\n              ,(fsps.end_qty + fsps.end_qty_tran_out + fsps.end_qty_tran_in) AS qty\\n              ,\'期初\' time_point\\n            FROM cdm.ods_stk_stock_period_summary fsps \\n  ) t \\nleft join cdm.ods_bas_logic_warehouse blw on t.logic_warehouse_id = blw.id\\nleft join cdm.ods_bas_storehouse bsh on t.logic_whareatype_id = bsh.id\\nleft join cdm.ods_gds_goods gg on t.goods_id = gg.id\\nleft join cdm.ods_bas_brand bb on gg.brand_id = bb.id\\nleft join cdm.ods_gds_singlproduct gsp on t.sku_id = gsp.id\\nleft join cdm.ods_bas_stock_organization bso on bso.id = t.stock_organization_id\\nleft join cdm.ods_bas_accounting_company bac on bac.id = bso.accounting_company_id\\nleft join cdm.ods_bas_legal_person_company blpc on blpc.id = bac.legal_person_company_id\\nleft join cdm.ods_bas_legal_person blp on blp.id = t.owner_id\\nleft join cdm.ods_stock_transaction_class sstc on t.transaction_class_id = sstc.id\\nleft join cdm.ods_bas_shop bs on t.business_party_id = bs.id\\nleft join cdm.ods_bas_customer bc on t.business_party_id = bc.id\\nleft join cdm.ods_bas_supplier bsu on t.business_party_id = bsu.id\\nleft join cdm.ods_bas_master_data_ext_attribute bmdea on t.logic_warehouse_id = bmdea.source_id and bmdea.source_type = \'9\'\\nleft join cdm.ods_bas_attribute_value v1 on bmdea.field_1 = toString(v1.id) \\nleft join cdm.ods_bas_attribute_value v2 on bmdea.field_2 = toString(v2.id)  \\nleft join cdm.ods_bas_attribute_value v3 on bmdea.field_3 = toString(v3.id)  \\nleft join cdm.ods_bas_