re_goods.po_no\\n            ELSE lshmx.po_no\\n      END poNo\\n      ,CASE\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN pre_goods.so_no\\n            ELSE lshmx.so_no\\n      END soNo\\n      ,lsd.accounting_date AS transactionDate\\n--      ,CASE\\n--            WHEN lshmx.relation_bill_name IN (\'71\') THEN tsb.bill_date\\n--            WHEN lshmx.relation_bill_name IN (\'77\') THEN tsrb.bill_date\\n--            ELSE NULL\\n--      END billDate\\n      ,customer.name AS customerName\\n      ,shop.name AS shopName\\n      ,gs.code AS skuCode\\n      ,channel.code\\n      ,lshmx.amount AS settleAmount\\n      ,lshmx.settle_qty AS settleQty\\n      ,NULL AS returnSign\\n      ,CASE records.check_type\\n            WHEN \'1\' THEN \'明细对账\'\\n            WHEN \'2\' THEN \'汇总对账-一对一或者一对多\'\\n            WHEN \'3\' THEN \'汇总对账-多对一\'\\n            WHEN \'4\' THEN \'汇总对账-多对多\'\\n            WHEN \'5\' THEN \'冲销对账\'\\n            WHEN \'6\' THEN \'预结算对账\'\\n            ELSE \'未知\'\\n      END checkType\\n      ,CASE\\n            WHEN lshmx.source_bill_name IS NULL THEN \'不需要对账\'\\n            WHEN lshmx.relation_bill_name IN (\'4S\') THEN \'不需要对账\'\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN \'预结算\'\\n            WHEN lshmx.relation_bill_name IN ( \'71\', \'77\' ) THEN \'对账成功\'\\n      END checkStatus\\n      ,IF( records.is_manual_check = \'1\', \'是\', \'否\' ) AS isManualCheck\\nFROM cdm.ods_settlement_so_line lshmx\\nLEFT JOIN cdm.ods_gds_singlproduct gs ON gs.id = lshmx.sku_id\\nLEFT JOIN cdm.ods_gds_goods gg ON gg.id = gs.goods_id\\nLEFT JOIN cdm.ods_gds_barcode tm ON tm.sku_id = gs.id AND type = \'1\'\\nLEFT JOIN cdm.ods_settlement_so_bill lsd ON lsd.id = lshmx.bill_id\\nLEFT JOIN cdm.ods_bas_shop shop ON shop.id = lsd.shop_id\\nLEFT JOIN cdm.ods_bas_customer customer ON customer.id = lsd.customer_id\\nLEFT JOIN cdm.ods_bas_customer_coop_info ci ON customer.id = ci.customer_id