ion_2c\\nSELECT\\n      \'零售单\' `type`\\n      ,cast(lshmx.id as String)\\n      ,\'1\' queryType\\n      ,lsd.bill_no AS billNo\\n      ,lsd.deal_code AS transactionNo\\n      ,lshmx.check_date AS returnedDate\\n      ,lsd.bill_date AS billDate\\n      ,shop.code AS shopCode\\n      ,shop.name AS shopName\\n      ,tm.barcode AS barcode\\n      ,tm.sku_code AS skuCode\\n      ,tm.sku_name AS skuName\\n      ,CASE sx.stock_control_type\\n            WHEN \'1\' THEN \'是\'\\n            WHEN \'2\' THEN \'是\'\\n            ELSE \'否\'\\n      END stockControlType\\n      ,CASE\\n            WHEN reversal_date IS NULL THEN lshmx.sale_qty\\n            WHEN IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 )= lshmx.pre_settle_amount THEN lshmx.sale_qty\\n            WHEN IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 )= lshmx.pre_settle_amount *(- 1) THEN lshmx.sale_qty *(- 1)\\n            ELSE NULL\\n      END settleQty\\n      ,IF(reversal_date IS NULL,cast(lshmx.check_amount as Float64),cast(IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) as Float64)) AS settleAmount\\n      ,REPLACE (REPLACE (REPLACE (REPLACE (REPLACE ( REPLACE ( IF ( lshmx.check_bill_name = \'4Q\', fund_records.check_type, sale_records.check_type ), \'1\', \'明细对账\' ), \'2\', \'汇总对账一对一或者一对多\' ),\'3\',\'汇总对账多对一\'),\'4\',\'汇总对账多对多\'),\'5\',\'冲销对账\'),\'6\',\'预结算对账\') checkType\\n      ,REPLACE ( REPLACE ( IF ( lshmx.check_bill_name = \'4Q\', fund_records.is_manual_check, sale_records.is_manual_check ), \'1\', \'是\' ), \'0\', \'否\' ) isManualCheck\\nFROM cdm.ods_trade_retail_bill_line lshmx\\nLEFT JOIN cdm.ods_gds_barcode tm ON tm.sku_id = lshmx.sku_id AND type = \'1\'\\nLEFT JOIN cdm.ods_trade_retail_bill lsd ON lsd.id = lshmx.bill_id\\nLEFT JOIN cdm.ods_bas_shop shop ON shop.id = lsd.shop_id\\nLEFT JOIN cdm.ods_bas_shop_business_info shopjy ON shopjy.shop_id = shop.id\\nLEFT JOIN cdm.ods_gds_goods_base_attribute sx ON sx.goods_code = tm.goods_code\\nLEFT JOIN (\\n      SELECT\\n            check_bill_line_id_to\\n            ,arrayStringConcat(groupArray(check_type),\',\') check_type\\n            ,arrayStringConcat(groupArray(is_manual_check),\',\') is_manual_check\\n            ,sum(check_amount) fun_check_amount\\n      FROM cdm.ods_trade_fund_check_records\\n      WHERE check_bill_line_table_name_to = \'TRADE_RETAIL_BILL_LINE\'\\n      GROUP BY check_bill_line_id_to\\n) fund_records ON fund_records.check_bill_line_id_to = lshmx.id\\nLEFT JOIN (\\n      SELECT\\n            check_bill_line_id_to\\n            ,arrayStringConcat(groupArray(check_type),\',\') check_type\\n            ,arrayStringConcat(groupArray(is_manual_check),\',\') is_manual_check\\n            ,sum(check_amount) sale_check_amount\\n      FROM cdm.ods_trade_sale_check_records\\n      WHERE check_bill_line_table_name_to = \'TRADE_RETAIL_BILL_LINE\'\\n      GROUP BY check_bill_line_id_to\\n) sale_records ON sale_records.check_bill_line_id_to = lshmx.id\\nWHERE 1 = 1\\n      AND (\\n            lshmx.reversal_date IS NULL\\n            AND lshmx.check_status IN (\'Y\')\\n            OR (\\n                  lshmx.reversal_date IS NOT NULL\\n                  AND lshmx.check_status IN (\'Y\')\\n                  AND IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) = lshmx.check_amount\\n                  AND IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) != 0\\n            )\\n            OR (\\n                  lshmx.reversal_date IS NOT NULL\\n                  AND lshmx.check_status IN (\'N\')\\n                  AND (IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) = lshmx.pre_settle_amount OR IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) = lshmx.pre_settle_amount *(- 1))\\n                  AND IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) != 0\\n            )\\n      )","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","groupId":null,"localParams":[],"connParams":"","preStatements":["truncate table cdm.dwd_settlement_ec_reconciliation_2c","select sleep(3)"],"postStatements":["insert into cdm.dwd_settlement_ec_reconciliation_2c\\nSELECT\\n      \'零售退单\' `type`\\n      ,cast(lshmx.id as String)\\n      ,\'2\' queryType\\n      ,lsd.bill_no AS billNo\\n      ,lsd.deal_code AS transactionNo\\n      ,lshmx.check_date AS returnedDate\\n      ,lsd.bill_date AS billDate\\n      ,shop.code AS shopCode\\n      ,shop.name AS shopName\\n      ,tm.barcode AS barcode\\n      ,tm.sku_code AS skuCode\\n      ,tm.sku_name AS skuName\\n      ,CASE sx.stock_control_type\\n            WHEN \'1\' THEN \'是\'\\n            WHEN \'2\' THEN \'是\'\\n            ELSE \'否\'\\n      END stockControlType\\n      ,CASE\\n            WHEN reversal_date IS NULL THEN ship.num *(- 1)\\n            WHEN IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 )= lshmx.pre_settle_amount THEN lshmx.return_num\\n            WHEN IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 )= lshmx.pre_settle_amount *(- 1) THEN lshmx.return_num *(- 1)\\n            ELSE NULL\\n      END settleQty\\n      ,IF(reversal_date IS NULL,cast(lshmx.check_amount as Float64),cast(IFNULL( fun_check_amount, 0 )+ IFNULL( sale_check_amount, 0 ) as Float64)) AS settleAmount\\n      ,REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(IF(lshmx.check_bill_name = \'4Q\', fund_records.check_type, sale_records.check_type ), \'1\', \'明细对账\' ), \'2\', \'汇总对账一对一或者一对多\' ),\'3\',\'汇总对账多对一\'),\'4\',\'汇总对账多对多\'),\'5\',\'冲销对账\'),\'6\',\'预结算对账\') checkType\\n      ,REPLACE(REPLACE(IF(lshmx.check_bill_name = \'4Q\', fund_records.is_manual_check, sale_records.is_manual_check), \'1\', \'是\' ), \'0\', \'否\') isManualCheck\\nFROM cdm.ods_trade_retail_return_bill_line lshmx\\nLEFT JOIN cdm.ods_gds_barcode tm ON tm.sku_id = lshmx.sku_id AND type = \'1\'\\nLEFT JOIN cdm.ods_trade_retail_return_bill lsd ON lsd.id = lshmx.bill_id\\nLEFT JOIN cdm.ods_bas_shop shop ON shop.id = lsd.shop_id\\nLEFT JOIN c