y_id,\\n        any(legal_person_company_id) legal_person_company_id,\\n        any(accounting_company_id) accounting_company_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        bill_name,\\n        bill_no,\\n        bill_type,\\n        owner_id,\\n        any(brand_id) brand_id,\\n        stock_type,\\n        t1.sku_id sku_id,\\n        any(goods_id) AS goods_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        dr_cr,\\n        t1.unit_id unit_id,\\n        toDate(stock_date) AS stock_date,\\n        sum(change_qty) AS sum_qty\\n    FROM cdm.ods_stk_stock_logic_workflow t1\\n    LEFT JOIN cdm.ods_bas_stock_organization t2 ON t1.stock_organization_id = t2.id\\n    LEFT JOIN cdm.dim_singlproduct t3 ON t1.sku_id = t3.sku_id\\n    WHERE   change_qty > 0 and bill_name !=\'61\' and dr_cr=\'DR\'\\n    GROUP BY\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        bill_name,\\n        bill_no,\\n        bill_type,\\n        owner_id,\\n        stock_type,\\n        t1.sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        t1.unit_id,\\n        dr_cr,\\n        stock_date\\n) AS sslw\\nJOIN\\n( -- 不分货主\\n    SELECT\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        stock_type,\\n        sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        unit_id,\\n        sum(qty) AS sum_qty\\n    FROM cdm.ods_stk_stock_logic_account\\n    GROUP BY\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        stock_type,\\n        sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        unit_id\\n    HAVING sum_qty > 0\\n) AS ssla ON sslw.stock_organization_id = ssla.stock_organization_id AND sslw.logic_warehouse_id = ssla.logic_wareh