arehouse_id = f.id \\nleft join (select code,name from cdm.ods_sys_dict_detail where dict_id = \'10\') as a1\\non a.bill_name =a1.code\\nleft join (select code,name from cdm.ods_sys_dict_detail where dict_id = \'9\') as a2\\non a.bill_type =a2.code\\nleft join cdm.ods_bas_legal_person as h \\non a.owner_id =h.id\\nleft join (select code,name from cdm.ods_sys_dict_detail where dict_id = \'182\') as a3\\non a.stock_type  =a3.code \\nleft join cdm.ods_gds_measu_unit as i \\non a.unit_id =i.id \\nleft join cdm.ods_bas_storehouse as k \\non a.logic_whareatype_id =k.id \\nwhere a.bill_name =\'61\' and a.bill_type =\'3R\'","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","groupId":null,"localParams":[],"connParams":"","preStatements":["truncate table cdm.dws_stockage_cal_detail ;","drop table if  exists cdm.dws_stockage_cal_detail_tmp1;","CREATE table if not exists cdm.dws_stockage_cal_detail_tmp1 Engine=MergeTree order by tuple() as \\nSELECT\\n    sslw.stock_organization_id,\\n    sslw.group_company_id,\\n    sslw.legal_person_company_id,\\n    sslw.accounting_company_id,\\n    sslw.logic_warehouse_id,\\n    sslw.logic_whareatype_id,\\n    sslw.bill_name,\\n    sslw.bill_no,\\n    sslw.bill_type,\\n    sslw.owner_id,\\n    sslw.brand_id,\\n    sslw.stock_type,\\n    sslw.sku_id,\\n    sslw.goods_id,\\n    sslw.attr1,\\n    sslw.attr2,\\n    sslw.attr3,\\n    sslw.attr4,\\n    sslw.attr5,\\n    sslw.unit_id,\\n    sslw.dr_cr,\\n    sslw.stock_date,\\n    sslw.sum_qty AS change_qty,\\n    ssla.sum_qty AS qty,\\n    sum(change_qty) OVER (PARTITION BY stock_organization_id,logic_warehouse_id,logic_whareatype_id,stock_type,sku_id,goods_id,attr1,attr2,attr3,attr4,attr5,unit_id ORDER BY stock_date DESC) AS sum_qty,\\n    rank() OVER (PARTITION BY stock_organization_id,logic_warehouse_id,logic_whareatype_id,stock_type,sku_id,goods_id,attr1,attr2,attr3,attr4,attr5,unit_id ORDER BY stock_date DESC) AS stock_sort\\nFROM\\n(\\n    SELECT\\n        stock_organization_id,\\n        any(group_company_id) group_company_id,\\n        any(legal_person_company_id) legal_person_company_id,\\n        any(accounting_company_id) accounting_company_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        bill_name,\\n        bill_no,\\n        bill_type,\\n        owner_id,\\n        any(brand_id) brand_id,\\n        stock_type,\\n        t1.sku_id sku_id,\\n        any(goods_id) AS goods_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        dr_cr,\\n        t1.unit_id unit_id,\\n        toDate(stock_date) AS stock_date,\\n        sum(change_qty) AS sum_qty\\n    FROM cdm.ods_stk_stock_logic_workflow t1\\n    LEFT JOIN cdm.ods_bas_stock_organization t2 ON t1.stock_organization_id = t2.id\\n    LEFT JOIN cdm.dim_singlproduct t3 ON t1.sku_id = t3.sku_id\\n    WHERE   change_qty > 0 and bill_name !=\'61\' and dr_cr=\'DR\'\\n    GROUP BY\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        bill_name,\\n        bill_no,\\n        bill_type,\\n        owner_id,\\n        stock_type,\\n        t1.sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        t1.unit_id,\\n        dr_cr,\\n        stock_date\\n) AS sslw\\nJOIN\\n( -- 不分货主\\n    SELECT\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        stock_type,\\n        sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        unit_id,\\n        sum(qty) AS sum_qty\\n    FROM cdm.ods_stk_stock_logic_account\\n    GROUP BY\\n        stock_organization_id,\\n        logic_warehouse_id,\\n        logic_whareatype_id,\\n        stock_type,\\n        sku_id,\\n        attr1,\\n        attr2,\\n        attr3,\\n        attr4,\\n        attr5,\\n        unit_id\\n    HAVING sum_qty > 0\\n) AS ssla ON sslw.stock_organization_id = ssla.stock_organization_id AND sslw.logic_warehouse_id = ssla.logic_warehouse_id AND sslw.logic_whareatype_id = ssla.logic_whareatype_id AND sslw.stock_type = ssla.stock_type AND sslw.sku_id = ssla.sku_id AND sslw.attr1 = ssla.attr1 AND sslw.attr2 = ssla.attr1 AND sslw.attr3 = ssla.attr3 AND sslw.attr4 = ssla.attr4 AND sslw.attr5 = ssla.attr5 AND sslw.unit_id = ssla.unit_id\\nORDER BY\\n    sslw.stock_organization_id ASC,\\n    sslw.logic_warehouse_id ASC,\\n    sslw.logic_whareatype_id ASC,\\n    sslw.stock_type ASC,\\n    sslw.sku_id ASC,\\n    sslw.attr1 ASC,\\n    sslw.attr2 ASC,\\n    sslw.attr3 ASC,\\n    sslw.attr4 ASC,\\n    sslw.attr5 ASC,\\n    sslw.unit_id ASC,\\n    sslw.stock_date DESC;","drop table if exists cdm.dws_stockage_cal_detail_tmp2 ;","CREATE table if not exists cdm.dws_stockage_cal_detail_tmp2 Engine=MergeTree order by tuple() as \\nSELECT\\n    stock_organization_id,\\n    group_company_id,\\n    legal_person_company_id,\\n    accounting_company_id,\\n    logic_warehouse_id,\\n    logic_whareatype_id,\\n    bill_name,\\n    bill_no,\\n    bill_type,\\n    owner_id,\\n    brand_id,\\n    stock_type,\\n    sku_id,\\n    goods_id,\\n    attr1,\\n    attr2,\\n    attr3,\\n    attr4,\\n    attr5,\\n    unit_id,\\n    stock_date,\\n    dr_cr,\\n    change_qty,\\n    qty,\\n    stock_sort,\\n    qty - sum_qty AS diff_qty,\\n    0 AS stock_sort2\\nFROM cdm.dws_stockage_cal_detail_tmp1 AS vslw\\nWHERE diff_qty >= 0\\nUNION ALL\\nSELECT\\n    stock_organization_id,\\n    group_company_id,\\n    legal_person_company_id,\\n    accounting_company_id,\\n    logic_warehouse_id,\\n    logic_whareatype_id,\\n    bill_name,\\n    bill_no,\\n    bill_type,\\n    owner_id,\\n    brand_id,\\n    stock_type,\\n    sku_id,\\n    goods_id,\\n    attr1,\\n    attr2,\\n    attr3,\\n    attr4,\\n    attr5,\\n    unit_id,\\n    stock_date,\\n    dr_cr,\\n    change_qty,\\n    qty,\\n    stock_sort,\\n    qty - sum_qty AS diff_qty,\\n    rank() OVER (PARTITION BY stock_organization_id,logi