,sslw.transaction_class_id transaction_class_id \\n              ,sslw.stock_type stock_type\\n              ,sslw.goods_id goods_id\\n              ,sslw.sku_id sku_id\\n              ,sslw.attr1 attr1\\n              ,sslw.attr2 attr2\\n              ,sslw.attr3 attr3\\n              ,sslw.attr4 attr4\\n              ,sslw.attr5 attr5\\n              ,sslw.stock_date  as stock_date\\n              ,\'\' as period_code\\n              ,sslw.change_qty qty\\n              ,\'期间\' time_point\\n           from cdm.ods_stk_stock_logic_workflow sslw\\n          where sslw.qty_type in (\'1\', \'31\', \'32\')         \\n          union all         \\n          SELECT \\n               fsps.stock_organization_id stock_organization_id\\n              ,fsps.logic_warehouse_id logic_warehouse_id\\n              ,fsps.logic_whareatype_id  logic_whareatype_id\\n              ,fsps.owner_id  owner_id\\n              ,null business_party_id\\n              ,fsps.transaction_class_id transaction_class_id \\n              ,fsps.stock_type  stock_type\\n              ,fsps.goods_id goods_id \\n              ,fsps.sku_id sku_id\\n              ,fsps.attr1 attr1\\n              ,fsps.attr2 attr2\\n              ,fsps.attr3 attr3\\n              ,fsps.attr4 attr4\\n              ,fsps.attr5 attr5\\n              ,null as stock_date \\n              ,fsps.period_code as period_code\\n              ,(fsps.end_qty + fsps.end_qty_tran_out + fsps.end_qty_tran_in) AS qty\\n              ,\'期末\' time_point\\n            FROM cdm.ods_stk_stock_period_summary fsps \\n  ) t \\nleft join cdm.ods_bas_logic_warehouse blw on t.logic_warehouse_id = blw.id\\nleft join cdm.ods_bas_storehouse bsh on t.logic_whareatype_id = bsh.id\\nleft join cdm.ods_gds_goods gg on t.goods_id = gg.id\\nleft join cdm.ods_bas_brand bb on gg.brand_id = bb.id\\nleft join cdm.ods_gds_singlproduct gsp on t.sku_id = gsp.id\\nleft join cdm.ods_bas_stock_organization bso on bso.id = t.stock_organization_id\\nleft join cdm.ods_bas_accounting_company bac on bac.id = bso.accounting_company_id\\nleft join cdm.ods_bas_legal_person_company blpc on blpc.id = bac.legal_person_company_id\\nleft join cdm.ods_bas_legal_person blp on blp.id = t.owner_id\\nleft join cdm.ods_stock_transaction_class sstc on t.transaction_class_id = sstc.id\\nleft join cdm.ods_bas_shop bs on t.business_party_id = bs.id\\nleft join cdm.ods_bas_customer bc on t.business_party_id = bc.id\\nleft join cdm.ods_bas_supplier bsu on t.business_party_id = bsu.id\\nleft join cdm.ods_bas_master_data_ext_attribute bmdea on t.logic_warehouse_id = bmdea.source_id and bmdea.source_type = \'9\'\\nleft join cdm.ods_bas_attribute_value v1 on bmdea.field_1 = toString(v1.id) \\nleft join cdm.ods_bas_attribute_value v2 on bmdea.field_2 = toString(v2.id)  \\nleft join cdm.ods_bas_attribute_value v3 on bmdea.field_3 = toString(v3.id)  \\nleft join cdm.ods_bas_attribute_value v4 on bmdea.field_4 = toString(v4.id)  \\nleft join cdm.ods_bas_attribute_value v5 on bmdea.field_5 = toString(v5.id)  \\nleft join cdm.ods_gds_goods_base_attribute ggba on t.goods_id = ggba.goods_id\\norder by sstc.code\\n","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","groupId":null,"localParams":[],"connParams":"","preStatements":["truncate table cdm.dwd_stk_sales_inventory_detail","select sleep(3)"],"postStatements":[],"dependence":{"dependTaskList":[],"relation":"AND"},"conditionResult":{"successNode":[],"failedNode":[]},"waitStartTimeout":{},"switchResult":{}}', 1, 2, 'default', -1, 3, 5, 0, 0, 0, 0, '', '2023-05-10 17:09:25', '2023-07-05 17:33:25') ;INSERT INTO dolphinscheduler.t_ds_task_definition (code, name, version, description, project_code, user_id, task_type, task_params, flag, task_priority, worker_group, environment_code, fail_retry_times, fail_retry_interval, timeout_flag, timeout_notify_strategy, timeout, delay_time, resource_ids, create_time, update_time) VALUES(9574020690976, 'dwd_settlement_ec_reconciliation_2b', 1, '', 1669102277000, 1, 'SQL', '{"type":"CLICKHOUSE","datasource":5,"sql":"insert into cdm.dwd_settlement_ec_reconciliation_2b\\nSELECT\\n      lshmx.id\\n      ,lsd.bill_no AS settleBillNo\\n      ,lsd.stock_organization_id\\n      ,lshmx.logic_warehouse_id\\n      ,blw.stock_organization_id\\n      ,CASE\\n            WHEN lshmx.source_bill_name IS NULL THEN \'手工\'\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND lshmx.relation_bill_name IN (\'4S\') THEN \'账单优惠明细\'\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND lshmx.relation_bill_name IS NULL THEN \'销售预结算\'\\n            WHEN lshmx.relation_bill_name IN ( \'71\', \'77\' ) THEN \'账单销售明细\'\\n      END type\\n      ,IFNULL( lsd.source_bill_no, lsd.bill_no ) AS billNo\\n      ,lshmx.relation_bill_no AS saleBillNo\\n      ,CASE\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN  pre_goods.po_no\\n            ELSE lshmx.po_no\\n      END poNo\\n      ,CASE\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN pre_goods.so_no\\n            ELSE lshmx.so_no\\n      END soNo\\n      ,lsd.accounting_date AS transactionDate\\n--      ,CASE\\n--            WHEN lshmx.relation_bill_name IN (\'71\') THEN tsb.bill_date\\n--            WHEN lshmx.relation_bill_name IN (\'77\') THEN tsrb.bill_date\\n--            ELSE NULL\\n--      END billDate\\n      ,customer.name AS customerName\\n      ,shop.name AS shopName\\n      ,gs.code AS skuCode\\n      ,channel.code\\n      ,lshmx.amount AS settleAmount\\n      ,lshmx.settle_qty AS settleQty\\n      ,NULL AS returnSign\\n      ,CASE records.check_type\\n            WHEN \'1\' THEN \'明细对账\'\\n            WHEN \'2\' THEN \'汇总对账-一对一或者一对多\'\\n            WHEN \'3\' THEN \'汇总对账-多对一\'\\n            WHEN \'4\' THEN \'汇总对账-多对多\'\\n            WHEN \'5\' THEN \'冲销对账\'\\n            WHEN \'6\' THEN \'预结算对账\'\\n            ELSE \'未知\'\\n      END checkType\\n      ,CASE\\n            WHEN lshmx.source_bill_name IS NULL THEN \'不需要对账\'\\n            WHEN lshmx.relation_bill_name IN (\'4S\') THEN \'不需要对账\'\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN \'预结算\'\\n            WHEN lshmx.relation_bill_name IN ( \'71\', \'77\' ) THEN \'对账成功\'\\n      END checkStatus\\n      ,IF( records.is_manual_check = \'1\', \'是\', \'否\' ) AS isManualCheck\\nFROM cdm.ods_settlement_so_line lshmx\\nLEFT JOIN cdm.ods_gds_singlproduct gs ON gs.id = lshmx.sku_id\\nLEFT JOIN cdm.ods_gds_goods gg ON gg.id = gs.goods_id\\nLEFT JOIN cdm.ods_gds_barcode tm ON tm.sku_id = gs.id AND type = \'1\'\\nLEFT JOIN cdm.ods_settlement_so_bill lsd ON lsd.id = lshmx.bill_id\\nLEFT JOIN cdm.ods_bas_shop shop ON shop.id = lsd.shop_id\\nLEFT JOIN cdm.ods_bas_customer customer ON customer.id = lsd.customer_id\\nLEFT JOIN cdm.ods_bas_customer_coop_info ci ON customer.id = ci.customer_id AND ci.source_id = lsd.base_company_id AND ci.source_type = \'7\'\\nLEFT JOIN cdm.ods_bas_channel channel ON ci.sales_channels_id = channel.id\\nLEFT JOIN cdm.ods_bas_shop_business_info shopjy ON shopjy.shop_id = shop.id\\nLEFT JOIN cdm.ods_gds_goods_base_attribute sx ON sx.goods_code = tm.goods_code\\nLEFT JOIN cdm.ods_bas_platform bp ON bp.id = shopjy.platform_id\\nLEFT JOIN cdm.ods_bas_brand bb ON bb.id = gg.brand_id\\nLEFT JOIN cdm.ods_gds_classification gc ON gc.id = sx.classification_id\\nLEFT JOIN cdm.ods_trade_sale_check_records records ON lshmx.source_bill_line_id = records.id AND lshmx.relation_bill_name IN ( \'71\', \'77\' )\\nLEFT JOIN cdm.ods_trade_sale_check_bill_goods pre_goods ON lshmx.source_bill_line_id = pre_goods.id\\nLEFT JOIN cdm.ods_trade_sale_bill tsb ON lshmx.relation_bill_id = CAST( tsb.id AS String )\\nLEFT JOIN cdm.ods_trade_sale_return_bill tsrb ON lshmx.relation_bill_id = CAST( tsrb.id AS String )\\nLEFT JOIN cdm.ods_bas_logic_warehouse blw ON lshmx.logic_warehouse_id = blw.id\\nORDER BY lshmx.id;","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","