D billDate\\n      ,customer.name AS customerName\\n      ,shop.name AS shopName\\n      ,gs.code AS skuCode\\n      ,channel.code\\n      ,lshmx.amount AS settleAmount\\n      ,lshmx.settle_qty AS settleQty\\n      ,NULL AS returnSign\\n      ,CASE records.check_type\\n            WHEN \'1\' THEN \'明细对账\'\\n            WHEN \'2\' THEN \'汇总对账-一对一或者一对多\'\\n            WHEN \'3\' THEN \'汇总对账-多对一\'\\n            WHEN \'4\' THEN \'汇总对账-多对多\'\\n            WHEN \'5\' THEN \'冲销对账\'\\n            WHEN \'6\' THEN \'预结算对账\'\\n            ELSE \'未知\'\\n      END checkType\\n      ,CASE\\n            WHEN lshmx.source_bill_name IS NULL THEN \'不需要对账\'\\n            WHEN lshmx.relation_bill_name IN (\'4S\') THEN \'不需要对账\'\\n            WHEN lshmx.source_bill_name IN (\'4S\') AND relation_bill_name IS NULL THEN \'预结算\'\\n            WHEN lshmx.relation_bill_name IN ( \'71\', \'77\' ) THEN \'对账成功\'\\n      END checkStatus\\n      ,IF( records.is_manual_check = \'1\', \'是\', \'否\' ) AS isManualCheck\\nFROM cdm.ods_settlement_so_line lshmx\\nLEFT JOIN cdm.ods_gds_singlproduct gs ON gs.id = lshmx.sku_id\\nLEFT JOIN cdm.ods_gds_goods gg ON gg.id = gs.goods_id\\nLEFT JOIN cdm.ods_gds_barcode tm ON tm.sku_id = gs.id AND type = \'1\'\\nLEFT JOIN cdm.ods_settlement_so_bill lsd ON lsd.id = lshmx.bill_id\\nLEFT JOIN cdm.ods_bas_shop shop ON shop.id = lsd.shop_id\\nLEFT JOIN cdm.ods_bas_customer customer ON customer.id = lsd.customer_id\\nLEFT JOIN cdm.ods_bas_customer_coop_info ci ON customer.id = ci.customer_id AND ci.source_id = lsd.base_company_id AND ci.source_type = \'7\'\\nLEFT JOIN cdm.ods_bas_channel channel ON ci.sales_channels_id = channel.id\\nLEFT JOIN cdm.ods_bas_shop_business_info shopjy ON shopjy.shop_id = shop.id\\nLEFT JOIN cdm.ods_gds_goods_base_attribute sx ON sx.goods_code = tm.goods_code\\nLEFT JOIN cdm.ods_bas_platform bp ON bp.id = shopjy.platform_id\\nLEFT JOIN cdm.ods_bas_brand bb ON bb.id = gg.brand_id\\nLEFT JOIN cdm.ods_gds_classification gc ON gc.id = sx.classification_id\\nLEFT JOIN cdm.ods_trade_sale_check_records records ON lshmx.source_bill_line_id = records.id AND lshmx.relation_bill_name IN ( \'71\', \'77\' )\\nLEFT JOIN cdm.ods_trade_sale_check_bill_goods pre_goods ON lshmx.source_bill_line_id = pre_goods.id\\nLEFT JOIN cdm.ods_trade_sale_bill tsb ON lshmx.relation_bill_id = CAST( tsb.id AS String )\\nLEFT JOIN cdm.ods_trade_sale_return_bill tsrb ON lshmx.relation_bill_id = CAST( tsrb.id AS String )\\nLEFT JOIN cdm.ods_bas_logic_warehouse blw ON lshmx.logic_warehouse_id = blw.id\\nORDER BY lshmx.id;","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","groupId":null,"localParams":[],"connParams":"","preStatements":["truncate table cdm.dwd_settlement_ec_reconciliation_2b","select sleep(3)"],"postStatements":[],"dependence":{"dependTaskList":[],"relation":"AND"},"conditionResult":{"successNode":[],"failedNode":[]},"waitStartTimeout":{},"switchResult":{}}', 1, 2, 'default', -1, 3, 5, 0, NULL, 0, 0, '', 1, '2023-05-16 17:00:06', '2023-05-16 17:00:06', '2023-05-16 17:00:06') ;INSERT INTO dolphinscheduler.t_ds_task_definition_log (code, name, version, description, project_code, user_id, task_type, task_params, flag, task_priority, worker_group, environment_code, fail_retry_times, fail_retry_interval, timeout_flag, timeout_notify_strategy, timeout, delay_time, resource_ids, operator, operate_time, create_time, update_time) VALUES(8943829343008, 'ods_bas_platform', 1, '', 1669102277000, 1, 'DATAX', '{"customConfig":1,"json":"{\\n\\t\\"job\\": {\\n\\t\\t\\"setting\\": {\\n\\t\\t\\t\\"errorLimit\\": {\\n\\t\\t\\t\\t\\"percentage\\": 0,\\n\\t\\t\\t\\t\\"record\\": 0\\n\\t\\t\\t},\\n\\t\\t\\t\\"speed\\": {\\n\\t\\t\\t\\t\\"channel\\": 3,\\n\\t\\t\\t\\t\\"batchSize\\": 1024\\n\\t\\t\\t}\\n\\t\\t},\\n\\t\\t\\"content\\": [\\n\\t\\t\\t{\\n\\t\\t\\t\\t\\"reader\\": {\\n\\t\\t\\t\\t\\t\\"name\\": \\"mysqlreader\\",\\n\\t\\t\\t\\t\\t\\"parameter\\": {\\n\\t\\t\\t\\t\\t\\t\\"username\\": \\"${Mysql_User}\\",\\n\\t\\