k_sales_inventory_detail\\nselect \\n         concat(\'[\',blpc.code,\']\',blpc.name) `法人公司`\\n        ,bso.accounting_company_id\\n        ,concat(\'[\',bac.code,\']\',bac.name) `核算公司` \\n        ,t.stock_organization_id\\n        ,concat(\'[\',bso.code,\']\',bso.name) `库存组织`\\n        ,t.logic_warehouse_id\\n        ,concat(\'[\',blw.code,\']\',blw.name) `逻辑仓`\\n        ,concat(\'[\',bsh.code,\']\',bsh.name) `库区`\\n        ,t.goods_id\\n        ,concat(\'[\',gg.code,\']\',gg.name) `商品`\\n        ,t.sku_id\\n        ,concat(\'[\',gsp.code,\']\',gsp.name) `SKU`\\n        ,IF(t.attr1 = \'-1\' , null, t.attr1) `辅助属性1`\\n        ,IF(t.attr2 = \'-1\' , null, t.attr2) `辅助属性2`\\n        ,IF(t.attr3 = \'-1\' , null, t.attr3) `辅助属性3`\\n        ,IF(t.attr4 = \'-1\' , null, t.attr4) `辅助属性4`\\n        ,IF(t.attr5 = \'-1\' , null, t.attr5) `辅助属性5`\\n        ,gg.brand_id\\n        ,concat(\'[\',bb.code,\']\',bb.name) `品牌`\\n        ,concat(\'[\',blp.code,\']\',blp.name) `货主`\\n        ,(CASE WHEN t.stock_type=\'1\' THEN \'经销\' \\n                     WHEN t.stock_type=\'2\' THEN \'代销\' \\n                     WHEN t.stock_type is NULL THEN NULL \\n                     ELSE \'其它\' END) stock_type\\n        ,(case when bs.id is not null then concat(\'[\',bs.code,\']\',bs.name) when bc.id is not null then concat(\'[\',bc.code,\']\',bc.name) when bsu.id is not null then concat(\'[\',bsu.code,\']\',bsu.name) else null end) `交易方`\\n        ,bmdea.field_1 `逻辑仓field1`\\n        ,bmdea.field_2 `逻辑仓field2`\\n        ,bmdea.field_3 `逻辑仓field3`\\n        ,bmdea.field_4 `逻辑仓field4`\\n        ,bmdea.field_5 `逻辑仓field5`\\n        ,ggba.field_1 `物料基础属性field1`\\n        ,ggba.field_2 `物料基础属性field2`\\n        ,ggba.field_3 `物料基础属性field3`\\n        ,ggba.field_4 `物料基础属性field4`\\n        ,ggba.field_5 `物料基础属性field5`\\n        ,ggba.field_59 `物料基础属性field59`\\n        ,gsp.field_1 `物料销售属性field1`\\n        ,gsp.field_2 `物料销售属性field2`\\n        ,gsp.field_3 `物料销售属性field3`\\n        ,gsp.field_4 `物料销售属性field4`\\n        ,gsp.field_5 `物料销售属性field5`\\n        ,t.stock_date\\n        ,t.period_code\\n        ,t.time_point `时间节点`\\n        ,sstc.code\\n        ,sstc.name `事务分类`\\n        ,t.qty `数量`\\n  from (\\n          select \\n               sslw.stock_organization_id stock_organization_id\\n              ,sslw.logic_warehouse_id logic_warehouse_id\\n              ,sslw.logic_whareatype_id logic_whareatype_id\\n              ,sslw.owner_id owner_id\\n              ,sslw.business_party_id business_party_id\\n              ,sslw.transaction_class_id transaction_class_id \\n              ,sslw.stock_type stock_type\\n              ,sslw.goods_id goods_id\\n              ,sslw.sku_id sku_id\\n              ,sslw.attr1 attr1\\n              ,sslw.attr2 attr2\\n              ,sslw.attr3 attr3\\n              ,sslw.attr4 attr4\\n              ,sslw.attr5 attr5\\n              ,sslw.stock_date  as stock_date\\n              ,\'\' as period_code\\n              ,sslw.change_qty qty\\n              ,\'期间\' time_point\\n           from cdm.ods_stk_stock_logic_workflow sslw\\n          where sslw.qty_type in (\'1\', \'31\', \'32\')         \\n          union all         \\n          SELECT \\n               fsps.stock_organization_id stock_organization_id\\n              ,fsps.logic_warehouse_id logic_warehouse_id\\n              ,fsps.logic_whareatype_id  logic_whareatype_id\\n              ,fsps.owner_id  owner_id\\n              ,null business_party_id\\n              ,fsps.transaction_class_id transaction_class_id \\n              ,fsps.stock_type  stock_type\\n              ,fsps.goods_id goods_id \\n              ,fsps.sku_id sku_id\\n              ,fsps.attr1 attr1\\n              ,fsps.attr2 attr2\\n              ,fsps.attr3 attr3\\n              ,fsps.attr4 attr4\\n              ,fsps.attr5 attr5\\n              ,null as stock_date \\n              ,fsps.period_code as period_code\\n              ,(fsps.end_qty + fsps.end_qty_tran_out + fsps.end_qty_tran_in) AS qty\\n              ,\'期末\' time_point\\n            FROM cdm.ods_stk_stock_period_summary fsps \\n  ) t \\nleft join cdm.ods_bas_logic_warehouse blw on t.logic_warehouse_id = blw.id\\nleft join cdm.ods_bas_storehouse bsh on t.logic_whareatype_id = bsh.id\\nleft join cdm.ods_gds_goods gg on t.goods_id = gg.id\\nleft join cdm.ods_bas_brand bb on gg.brand_id = bb.id\\nleft join cdm.ods_gds_singlproduct gsp on t.sku_id = gsp.id\\nleft join cdm.ods_bas_stock_organization bso on bso.id = t.stock_organization_id\\nleft join cdm.ods_bas_accounting_company bac on bac.id = bso.accounting_company_id\\nleft join cdm.ods_bas_legal_person_company blpc on blpc.id = bac.legal_person_company_id\\nleft join cdm.ods_bas_legal_person blp on blp.id = t.owner_id\\nleft join cdm.ods_stock_transaction_class sstc on t.transaction_class_id = sstc.id\\nleft join cdm.ods_bas_shop bs on t.business_party_id = bs.id\\nleft join cdm.ods_bas_customer bc on t.business_party_id = bc.id\\nleft join cdm.ods_bas_supplier bsu on t.business_party_id = bsu.id\\nleft join cdm.ods_bas_master_data_ext_attribute bmdea on t.logic_warehouse_id = bmdea.source_id and bmdea.source_type = \'9\'\\nleft join cdm.ods_bas_attribute_value v1 on bmdea.field_1 = toString(v1.id) \\nleft join cdm.ods_bas_attribute_value v2 on bmdea.field_2 = toString(v2.id)  \\nleft join cdm.ods_bas_attribute_value v3 on bmdea.field_3 = toString(v3.id)  \\nleft join cdm.ods_bas_attribute_value v4 on bmdea.field_4 = toString(v4.id)  \\nleft join cdm.ods_bas_attribute_value v5 on bmdea.field_5 = toString(v5.id)  \\nleft join cdm.ods_gds_goods_base_attribute ggba on t.goods_id = ggba.goods_id\\norder by sstc.code\\n","udfs":"","sqlType":"1","sendEmail":false,"displayRows":10,"title":"","groupId":null,"localParams":[],"connParams":"","preStatements":["truncate table cdm.dwd_stk_sales_inventory_detail","select sleep(3)"],"postStatements":[],"dependence":{"dependTaskList":[],"relation":"AND"},"conditionResult":{"successNode":[],"failedNode":[]},"waitStartTimeout":{},"switchResult":{}}', 1, 2, 'default', -1, 3, 5, 0, 0, 0, 0, '', 1, '2023-07-05 17:33:25', '2023-05-10 17:09:25', '2023-07-05 17:33:25') ;INSERT INTO dolphinscheduler.t_ds_task_definition_log (code, name, version, description, project_code, user_id, task_type, task_params, flag, task_priority, worker_group, environment_code, fail_retry_times, fail_retry_interval, timeout_flag, timeout_notify_strategy, timeout, delay_time, resource_ids, operator, operate_time, create_time, update_time) VALUES(10136557143712, 'ods_trade_purchase_bill', 1, '', 1669102277000, 1, 'DATAX', '{"customConfig":1,"json":"{\\n\\t\\"job\\": {\\n\\t\\t\\"setting\\": {\\n\\t\\t\\t\\"errorLimit\\": {\\n\\t\\t\\t\\t\\"percentage\\": 0,\\n\\t\\t\\t\\t\\"record\\": 0\\n\\t\\t\\t},\\n\\t\\t\\t\\"speed\\": {\\n\\t\\t\\t\\t\\"channel\\": 3,\\n\\t\\t\\t\\t\\"batchSize\\": 1024\\n\\t\\t\\t}\\n\\t\\t},\\n\\t\\t\\"content\\": [\\n\\t\\t\\t{\\n\\t\\t\\t\\t\\"reader\\": {\\n\\t\\t\\t\\t\\t\\"name\\": \\"mysqlreader\\",\\n\\t\\t\\t\\t\\t\\"parameter\\": {\\n\\t\\t\\t\\t\\t\\t\\"username\\": \\"${Mysql_User}\\",\\n\\t\\t\\t\\t\\t\\t\\"password\\": \\"${Mysql_PassWord}\\",\\n\\t\\t\\t\\t\\t\\t\\"splitPk\\": \\"\\",\\n\\t\\t\\t\\t\\t\\t\\"connection\\": [\\n\\t\\t\\t\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\t\\t\\t\\t\\"jdbcUrl\\": [\\n\\t\\t\\t\\t\\t\\t\\t\\t\\t\\"jdbc:mysql://${Mysql_Ip}/e3plus_trade_integrate?useUnicode=true&characterEncoding=UTF-8\\"\\n\\t\\t\\t\\t\\t\\t\\t\\t],\\n\\t\\t\\t\\t\\t\\t\\t\\t\\"querySql\\": [\\n\\t\\t\\t\\t\\t\\t\\t\\t\\t\\"select `id`,`bill_type`,`bill_name`,`bill_business_type_id`,`bill_no`,`bill_date`,`bill_status`,`delivery_status`,`settle_status`,`invoice_status`,`pay_status`,`ap_status`,`remark`,`supplier_id`,`supplier_company_id`,`base_company_id`,`stock_organization_id`,`logic_warehouse_id`,`logic_whareatype_id`,`purchaser_id`,`create_by`,`create_time`,`modify_by`,`modify_time`,`submit_by`,`submit_time`,`approve_by`,`approve_time`,`complete_by`,`complete_time`,`cancel